    ALTER TABLE organizations ADD COLUMN billing_period_start timestamp;
  END IF;
  IF NOT EXISTS (SELECT 1 FROM information_schema.columns WHERE table_name='organizations' AND column_name='billing_period_end') THEN
    ALTER TABLE organizations ADD COLUMN billing_period_end timestamp;
  END IF;
END $$;

